function [Ksr,Ksc,Ksv,kc] = assmblsurf_ful(ieles,nnps,nps,sek,Ksr,Ksc,Ksv,kc)% assekmbles elemental force and stiffness matrices into global counterpartsfor  i=1:1:nnps	            I=nps(ieles,i);            % Row number in the global stiffness matrix    i3=3*I;    i2=i3-1;    i1=i2-1;      	      for j=1:1:nnps                      J=nps(ieles,j);        % Row number in the global stiffness matrix        j3=3*J;        j2=j3-1;        j1=j2-1;        Ksr(kc)=i1;         Ksc(kc)=j1;         Ksv(kc)=sek(i,j);        kc=kc+1;        Ksr(kc)=i1;         Ksc(kc)=j2;         Ksv(kc)=sek(i,j+nnps);         kc=kc+1;        Ksr(kc)=i1;         Ksc(kc)=j3;         Ksv(kc)=sek(i,j+2*nnps);         kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j1;        Ksv(kc)=sek(i+nnps,j);          kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j2;         Ksv(kc)=sek(i+nnps,j+nnps);         kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j3;         Ksv(kc)=sek(i+nnps,j+2*nnps);         kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j1;        Ksv(kc)=sek(i+2*nnps,j);          kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j2;         Ksv(kc)=sek(i+2*nnps,j+nnps);         kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j3;         Ksv(kc)=sek(i+2*nnps,j+2*nnps);         kc=kc+1;                endend