function  [n rjs] = surfjac(ieles,nnps,nqpts,dnd2,nps,x,y)%  computes surface jacobian termsrjs = zeros(1,nqpts);n=zeros(nqpts,2);for i=1:1:nqpts          tjac = zeros(2,2);           for j=1:1:nnps  	      j1=nps(ieles,j) ;  	                          tjac(2,1)=tjac(2,1)+dnds(j,i)*x(j1);                                tjac(2,2)=tjac(2,2)+dnds(j,i)*y(j1);                   	    end          n1 = tjac(2,2)*tjac(3,3) - tjac(3,2)*tjac(2,3);      n2 = tjac(3,1)*tjac(2,3) - tjac(2,1)*tjac(3,3);      n3 = tjac(2,1)*tjac(3,2) - tjac(3,1)*tjac(2,2);      nvecmag = sqrt(n1*n1+n2*n2+n3*n3);        tjac(1,1)=n1/nvecmag;                                 tjac(1,2)=n2/nvecmag;	        tjac(1,3)=n3/nvecmag;                                                                           rjs(i)=det(tjac);      n(i,1:3)=tjac(1,1:3);      end