function [Cr,Cc,Cv,cc] = assmbl_qmat(iele,iref,nnpe,np,ce,Cr,Cc,Cv,cc)% assembles elemental force and stiffness matrices into global counterparts% Global dof - jJ3=3*iref;J2=J3-1;	  J1=J2-1;for  i=1:1:nnpe	    % Global nodes    I3=3*np(iele,i);    I2=I3-1;    I1=I2-1;	      % Local nodes    i3=i*3;    i2=i3-1;    i1=i2-1;	  %     for j=1:1:3	                    Cr(cc)=I1;            Cc(cc)=J1;            Cv(cc)=ce(i1,1);           cc=cc+1;                      Cr(cc)=I1;            Cc(cc)=J2;            Cv(cc)=ce(i1,2);            cc=cc+1;                      Cr(cc)=I1;            Cc(cc)=J3;            Cv(cc)=ce(i1,3);            cc=cc+1;                                            Cr(cc)=I2;            Cc(cc)=J1;           Cv(cc)=ce(i2,1);             cc=cc+1;                      Cr(cc)=I2;            Cc(cc)=J2;            Cv(cc)=ce(i2,2);            cc=cc+1;                      Cr(cc)=I2;            Cc(cc)=J3;            Cv(cc)=ce(i2,3);            cc=cc+1;	                                   Cr(cc)=I3;            Cc(cc)=J1;           Cv(cc)=ce(i3,1);             cc=cc+1;                      Cr(cc)=I3;            Cc(cc)=J2;            Cv(cc)=ce(i3,2);            cc=cc+1;                      Cr(cc)=I3;            Cc(cc)=J3;            Cv(cc)=ce(i3,3);            cc=cc+1;        %     endendreturnend