function [Fsr,Fsv,fc] = assmbl_vec(iele,nnpe,np,fe,Fsr,Fsv,fc)% assembles elemental force and stiffness matrices into global counterparts%nnpe2=2*nnpe;for  i=1:1:nnpe	        % Global dof        I3=3*np(iele,i);        I2=I3-1;        I1=I2-1;        % Local dof        i3=3*i;        i2=i3-1;        i1=i2-1;              Fsr(fc)=I1;         Fsv(fc)=fe(i1);         fc=fc+1;        Fsr(fc)=I2;         Fsv(fc)=fe(i2);           fc=fc+1;              Fsr(fc)=I3;         Fsv(fc)=fe(i3);         fc=fc+1;	  	  endreturnend