function [sk,f] = bc_ful(numnp,npbc,bfx,bfy,sk,f)%applies boundary conditionsfor i=1:1:numnp                                                                                                                             n1=npbc(i); 	        if(n1==1)                                                    f(2*i-1)=f(2*i-1)+bfx(i);                                          f(2*i)  =f(2*i)  +bfy(i);                                                end                                                                                                                                     if(n1==2)                                                       sk(2*i-1,2*i-1)=sk(2*i-1,2*i-1)*1.0e+8 ;	  f(2*i-1)   =bfx(i) *sk(2*i-1,2*i-1) ;        f(2*i)     =f(2*i)     +bfy(i);                                                end                                                                                                                                     if(n1==3)   	  sk(2*i,2*i)=sk(2*i,2*i)*1.0e+8;                                             f(2*i)   =bfy(i) *sk(2*i,2*i);                                              f(2*i-1) =f(2*i-1) +bfx(i);                                          end                                                                           if(n1==4)                                             	  	  sk(2*i-1,2*i-1)  =sk(2*i-1,2*i-1) *1.0e+8;                             f(2*i-1)     =bfx(i)  *sk(2*i-1,2*i-1);                                       sk(2*i,2*i)    =sk(2*i,2*i)   *1.0e+8;                             f(2*i)       =bfy(i)    *sk(2*i,2*i);       end 	  end                                                               